import { useState } from "react";
import "./App.css";
import Checkout from "./pages/Checkout";

import Details from './pages/Details';
import { Link, Route,Routes } from "react-router-dom";
function App() {
    
    const [amount,setAmount]=useState(0);
    console.log(amount);
    function changeHandler(event){
       //console.log(event.target.value);
        setAmount(event.target.value);
        
    }
  return (
    <div>
      <h2>Digital Media Promotion</h2>
      <div></div>

      <h1>Digital Media Promotion</h1>
      <div>ENTER AMOUNT</div>
      <div>
        <img src='' alt='img'/>
        <form>
            <input type='text'  placeholder='0.00' onChange={changeHandler} ></input>
        </form>
        
      </div>
      <div>
        <p>For any query,Contact +1-989-613-7474</p>
      </div>
      <div></div>
      <Link to='/details'>
      <button >Checkout</button>
      </Link>
      
      <div></div>
      <div>
        <img></img>
        <p>Secure payment by Square</p>
      </div>
      
       <Routes>

    
    <Route path='/details' element={<Details amount={amount}/>}/>
    <Route path='/checkout' element={<Checkout amount={amount}/>}/>
    </Routes>
    </div>
  )
}
  

    
   


export default App;
