import React from 'react';
import { useForm } from 'react-hook-form';
import { Link } from 'react-router-dom';

const Details = (props) => {
  const amount = props.amount;
  const { register, handleSubmit, formState: { errors } } = useForm();

  const onSubmit = (data) => {
    fetch('http://localhost:4000/api/v1/credit-cards', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data.message);
        // Handle success response
      })
      .catch((error) => {
        console.error('Error:', error);
        // Handle error
      });
  };

  return (
    <div className="bg-gray-100 font-sans">
      <div className="p-6 bg-white shadow-md">
        <h1 className="text-2xl font-bold">Digital Media Promotion</h1>
      </div>
      <div className="p-6">
        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="mb-6">
            {/* ...existing JSX code */}
          </div>
          <hr className="border-gray-300 my-8" />
          <p className="mb-2 font-semibold">REQUIRED*</p>
          <input
            type="text"
            className="w-full p-2 mb-4 border border-gray-300 rounded outline-none"
            name="email2"
            placeholder="Email ID"
            required
            {...register('email', { required: true })}
          />
          {errors.email && (
            <span className="text-red-500">Email is required.</span>
          )}
          <input
            type="text"
            className="w-full p-2 mb-4 border border-gray-300 rounded outline-none"
            name="phone2"
            placeholder="Phone No."
            required
            {...register('phoneNumber', { required: true })}
          />
          {errors.phoneNumber && (
            <span className="text-red-500">Phone Number is required.</span>
          )}
          {/* ...existing JSX code */}
          <hr className="border-gray-300 my-8" />
          <p className="mb-2 font-semibold">CONTACT</p>
          {/* ...existing JSX code */}
          <input
            type="email"
            className="w-full p-2 mb-4 border border-gray-300 rounded outline-none"
            name="email"
            placeholder="Email Address for receipt"
            required
            {...register('email', { required: true })}
          />
          {errors.email && (
            <span className="text-red-500">Email is required.</span>
          )}
          <div className="mb-4 flex">
            {/* ...existing JSX code */}
          </div>
          {/* ...remaining JSX code */}
          <button type="submit" className="btn bbtn" name="btnsave">
            Pay ${amount}
          </button>
        </form>
      </div>
      {/* ...remaining JSX code */}
    </div>
  );
};

export default Details;
