import React from 'react';
import { useForm } from 'react-hook-form';
import { Link } from 'react-router-dom';

const Details = (props) => {
  const { register, handleSubmit } = useForm();
  const createCreditCard = async (data) => {
    try {
      const savedCreditCardResponse = await fetch(`${process.env.REACT_APP_BASE_URL}/credit-cards`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (savedCreditCardResponse.ok) {
        console.log('Credit card details saved successfully');
        // Handle success response
      } else {
        console.error('Failed to save credit card details');
        // Handle error response
      }
    } catch (error) {
      console.error('Error:', error);
      // Handle error
    }
  };

  return (
    <div className="bg-gray-100 font-sans">
      <div className="p-6 bg-white shadow-md">
        <h1 className="text-2xl font-bold">Digital Media Promotion</h1>
      </div>
      <div className="p-6">
        <form onSubmit={handleSubmit(createCreditCard)}>
          <input
            type="text"
            className="w-full p-2 mb-4 border border-gray-300 rounded outline-none"
            name="card_number"
            placeholder="Card Number"
            required
            {...register('cardNumber')}
          />
          <input
            type="text"
            className="w-full p-2 mb-4 border border-gray-300 rounded outline-none"
            name="card_exp"
            placeholder="Expiration (MM/YY)"
            required
            {...register('expiration')}
          />
          <input
            type="text"
            className="w-full p-2 mb-4 border border-gray-300 rounded outline-none"
            name="card_cvv"
            placeholder="CVC"
            required
            {...register('cvc')}
          />
          <input
            type="text"
            className="w-full p-2 mb-4 border border-gray-300 rounded outline-none"
            name="phone"
            placeholder="Phone Number"
            required
            {...register('phoneNumber')}
          />
          <input
            type="text"
            className="w-full p-2 mb-4 border border-gray-300 rounded outline-none"
            name="fname"
            placeholder="First Name"
            required
            {...register('cardHolderFirstName')}
          />
          <input
            type="text"
            className="w-full p-2 mb-4 border border-gray-300 rounded outline-none"
            name="lname"
            placeholder="Last Name"
            required
            {...register('cardHolderLastName')}
          />
          <input
            type="text"
            className="w-full p-2 mb-4 border border-gray-300 rounded outline-none"
            name="email"
            placeholder="Email Address"
            required
            {...register('email')}
          />
          <input
            type="text"
            className="w-full p-2 mb-4 border border-gray-300 rounded outline-none"
            name="card_postal"
            placeholder="Postal Code"
            required
            {...register('postalCode')}
          />

          <button type="submit" className="btn">
            Pay
          </button>
        </form>
      </div>
    </div>
  );
};

export default Details;
